INSERT INTO quizzes (qnum, topic, prompt, choices, answer_index, hint, explanation, difficulty_label, difficulty_level) VALUES
(1, '민간요법·오해', '감기에 항생제를 먹으면 빨리 낫는다.', '["코로나일 때만 맞다", "증상만 줄면 항생제는 스스로 중단해도 된다", "대부분 아니다", "대부분 맞다"]', 2, '', '감기의 상당수는 바이러스 원인이어서 항생제가 효과 없고, 오남용은 내성만 늘립니다.', '보통', 2),
(2, '민간요법·오해', '가벼운 열탕·접촉 화상 직후 가장 먼저 할 일은?', '["흐르는 차가운 물로 10~20분 식힌다", "치약/버터를 바른다", "치약이나 된장을 바르면 화상이 가라앉는다", "얼음을 직접 대어 식힌다"]', 0, '', '차가운 물로 충분히 식히는 것이 표준 응급처치입니다(얼음·연고 금지).', '어려움', 3),
(3, '민간요법·오해', '코피가 날 때 올바른 자세는?', '["목 뒤에 얼음찜질만 한다", "앉아 상체를 앞으로 숙이고 코 앞부분을 10~15분 눌러준다", "머리를 뒤로 젖혀 피를 목으로 넘기면 빨리 멈춘다", "누워서 머리를 뒤로 젖힌다"]', 1, '', '앞으로 숙여 연부(코 앞부분)를 지속 압박합니다. 피가 목으로 넘어가게 하면 안 됩니다.', '어려움', 3),
(4, '민간요법·오해', '들에서 뱀에 물렸다. 무엇이 옳은가?', '["상처를 칼로 절개해 독을 짜내면 된다", "움직임 최소화·사지 고정 후 즉시 의료기관으로 이동", "상처를 입으로 빨아낸다", "상처를 절개해 피를 빼고 지혈대를 꽉 묶는다"]', 1, '', '움직임 최소화와 사지 고정이 핵심, 지혈대·절개·입으로 빨기는 금지입니다.', '보통', 2),
(5, '민간요법·오해', '남은 항생제를 비슷한 증상에 스스로 복용한다.', '["증상만 줄면 항생제는 스스로 중단해도 된다", "하면 안 된다", "한 번만은 괜찮다", "괜찮다"]', 1, '', '항생제는 바이러스성 질환에 듣지 않고 부작용·내성 위험이 큽니다. 처방대로만 복용하세요.', '보통', 2),
(6, '민간요법·오해', '식중독(구토·설사) 의심 시 우선 권장되는 대처는?', '["지사제를 바로 먹는다", "24시간 완전 금식한다", "지사제와 항생제를 함께 먹으면 빨리 회복된다", "수분·전해질 보충을 우선한다"]', 3, '', '탈수를 막는 수분·전해질 보충이 최우선입니다.', '보통', 2),
(7, '민간요법·오해', '벌레에 물려 가려울 때 가장 적절한 대처는?', '["바늘로 긁어서 진물 빼기", "냉찜질하고 긁지 않는다", "긁어서 붓기를 빼준다", "상처를 칼로 절개해 독을 짜내면 된다"]', 1, '', '긁으면 2차 감염·염증을 키울 수 있습니다.', '보통', 2),
(8, '민간요법·오해', '목에 생선가시가 걸렸을 때?', '["밥을 크게 삼켜 이물을 아래로 밀어내면 안전하다", "억지로 삼키지 말고 의료 도움을 받는다", "밥을 크게 삼켜 아래로 밀어내면 된다", "머리를 거꾸로 하고 기침을 멈춘다"]', 1, '', '무리한 삼키기는 상처·천공 위험을 높입니다.', '보통', 2),
(9, '민간요법·오해', '화상으로 물집이 잡혔다. 어떻게 할까?', '["치약이나 된장을 바르면 화상이 가라앉는다", "스스로 터뜨리지 않는다", "칼로 윗껍질을 제거한다", "터뜨려서 물을 빼고 말린다"]', 1, '', '물집은 감염 방지·치유에 도움. 임의로 터뜨리지 않습니다.', '어려움', 3),
(10, '민간요법·오해', '상처 소독을 위해 소주 등 음용 알코올을 붓는다.', '["소주를 붓는 것이 가장 안전한 소독법이다", "상처 크면만 좋다", "권장되지 않는다", "좋다"]', 2, '', '조직 손상·자극 위험. 깨끗한 물로 씻고 적절한 소독제를 사용하세요.', '보통', 2),
(11, '생활 응급', '성인 심정지 가슴압박 권장 속도는?', '["분당 100~120회", "분당 140~160회", "분당 60~80회", "분당 80~90회로 천천히 압박하는 게 좋다"]', 0, '', '생존율을 위해 100~120회/분이 권장됩니다.', '어려움', 3),
(12, '생활 응급', '자동심장충격기(AED)는…?', '["패드를 붙이고 기기 음성안내를 따르면 누구나 사용할 수 있다", "맥박을 먼저 길게 확인해야만 쓴다", "분당 80~90회로 천천히 압박하는 게 좋다", "의료인만 사용할 수 있다"]', 0, '', 'AED는 일반인 사용을 전제로 설계되며, 음성 안내를 따르면 됩니다.', '어려움', 3),
(13, '생활 응급', '성인 기도폐쇄(숨 못 쉬고 말 못 함) 시 올바른 응급처치는?', '["등 5회 두드리기와 복부 밀치기를 교대로 시행", "물을 마시게 한다", "거꾸로 들고 흔든다", "물을 마시게 하면 대부분 해결된다"]', 0, '', '의식 있는 성인은 등 타격 5회 + 복부 밀치기를 반복합니다.', '어려움', 3),
(14, '생활 응급', '눈에 화학물질이 튀었다.', '["눈을 크게 벌리고 15분 이상 깨끗한 물로 지속 세척", "젖은 수건으로 닦는다", "수렴성 안약을 즉시 넣는다", "수건으로 가볍게만 닦으면 충분하다"]', 0, '', '지속적인 수세(≥15분)가 핵심입니다. 렌즈 착용 시 제거합니다.', '보통', 2),
(15, '생활 응급', '쥐어짜는 듯한 가슴 통증·압박감이 10분 이상 지속된다.', '["집에서 휴식하며 지켜본다", "즉시 119에 연락해 도움을 받는다", "민간요법을 따르면 대부분 저절로 낫는다", "자가운전으로 병원에 간다"]', 1, '', '심근경색 의심 상황으로 즉시 응급전화가 우선입니다.', '어려움', 3),
(16, '생활 응급', '성인 치아가 완전히 빠졌다(탈구). 최선의 조치는?', '["빠진 치아는 소독약에 오래 담가 둔다", "솔로 문질러 씻은 뒤 마른 거즈에 싼다", "가능하면 원래 자리에 재삽입(어렵다면 우유/식염수/침에 보관) 후 즉시 치과", "얼음 위에 올려 둔다"]', 2, '', '빠른 재삽입 또는 우유·식염수·침 보관 후 즉시 치료가 유리합니다.', '어려움', 3),
(17, '생활 응급', '팔 골절이 의심될 때?', '["모양을 맞춰 ‘뚝’ 하고 돌려본다", "부목으로 고정·움직임 최소화 후 의료기관으로 이동", "강하게 주무르면 뼈가 제자리로 돌아간다", "온찜질로 풀어준다"]', 1, '', '고정·안정이 기본, 무리한 정복은 손상 악화.', '보통', 2),
(18, '생활 응급', '화상으로 옷이 피부에 달라붙었다.', '["치약이나 된장을 바르면 화상이 가라앉는다", "즉시 떼어낸다", "붙은 부분은 그대로 두고 냉각·의료 도움", "연고를 듬뿍 발라 떼어낸다"]', 2, '', '달라붙은 옷을 억지 제거하면 2차 손상이 생깁니다.', '어려움', 3),
(19, '생활 응급', '발작(경련) 목격 시 적절한 대처는?', '["머리 주변 위험물을 치우고 시간(발작 지속)을 기록한다", "밥을 크게 삼켜 이물을 아래로 밀어내면 안전하다", "억지로 몸을 눌러 움직임을 막는다", "물을 마시게 한다"]', 0, '', '억지 제압·삽입물 금지. 안전 확보·시간 기록이 중요합니다.', '보통', 2),
(20, '생활 응급', '눈에 모래가 들어갔다.', '["비벼서 빨리 빼낸다", "수건으로 가볍게만 닦으면 충분하다", "안약을 먼저 듬뿍 넣는다", "깨끗한 물로 눈을 벌리고 충분히 세척"]', 3, '', '비비면 각막 손상 위험. 수세가 우선입니다.', '어려움', 3),
(21, '생활 응급', '피부에 화학물질이 묻었다.', '["식초 등으로 중화제를 바른다", "수건으로 가볍게만 닦으면 충분하다", "오염된 옷을 벗기고 비누·물로 즉시 씻어낸다", "마른 수건으로만 가볍게 닦는다"]', 2, '', '오염 의복 제거 + 물/비누로 세척이 표준입니다(중화제 권장 X).', '보통', 2),
(22, '생활 응급', '의식이 없고 숨을 쉬지 않는다(또는 비정상 호흡). 먼저 할 일은?', '["증상만 줄면 항생제는 스스로 중단해도 된다", "맥박을 오래 확인한다", "흔들어 깨운 뒤 옆으로 눕혀만 둔다", "119 신고와 즉시 가슴압박 시작"]', 3, '', '지연 없이 신고→가슴압박(CAB)이 원칙입니다.', '어려움', 3),
(23, '생활 건강', '체온이 약간 높고 몸살 기운이 있다. 잘못된 대처는?', '["수분·휴식 취하기", "민간요법을 따르면 대부분 저절로 낫는다", "해열제 복용 시 성분·용량 확인", "항생제를 임의로 복용"]', 3, '', '감기·몸살 등 바이러스성 증상에 항생제는 효과 없습니다.', '보통', 2),
(24, '생활 건강', '알코올 함유 손소독제를 사용할 때 올바른 방법은?', '["물로 한번 헹군다", "바른 즉시 티슈로 닦아낸다", "손 전체가 마를 때까지 문질러 준다", "소주를 붓는 것이 가장 안전한 소독법이다"]', 2, '', '충분량 도포 후 마를 때까지 문지르는 것이 핵심입니다.', '보통', 2),
(25, '생활 건강', '혈압 측정 전 올바른 준비는?', '["커피 한 잔 후 바로 잰다", "운동 직후 재면 더 정확하다", "누워서 다리를 꼬고 잰다", "5분간 편안히 앉아 안정 후 측정"]', 3, '', '카페인·운동 직후는 피하고 안정 상태에서 측정합니다.', '보통', 2),
(26, '생활 건강', '비누와 물로 손 씻기의 권장 시간은 최소?', '["20초", "10초", "40초", "민간요법을 따르면 대부분 저절로 낫는다"]', 0, '', '손 전체를 20초 이상 문질러 세정합니다.', '보통', 2),
(27, '생활 건강', '비누/물 없을 때 사용할 손소독제 조건은?', '["60% 이상", "알코올 20% 이상", "40% 이상", "소주를 붓는 것이 가장 안전한 소독법이다"]', 0, '', '알코올 ≥60% 제품을 사용합니다.', '보통', 2),
(28, '생활 건강', '해열제(아세트아미노펜·이부프로펜) 복용 시 주의는?', '["민간요법을 따르면 대부분 저절로 낫는다", "표기 용량을 지키고, 중복 성분 동시 복용을 피한다", "서로 중복된 성분을 이중으로 복용할 수 있다", "술과 함께 복용해도 무관하다"]', 1, '', '성분·용량 준수가 안전합니다(특히 간독성 주의).', '어려움', 3),
(29, '생활 건강', '상처가 길게 났는데 계속 피가 난다.', '["소금물에 담가둔다", "소주를 붓는 것이 가장 안전한 소독법이다", "파스를 붙인다", "흐르는 물로 씻고 지속 압박하여 지혈한다"]', 3, '', '직접 압박 지혈이 기본(오염 제거 포함).', '보통', 2),
(30, '생활 건강', '실내에서 갑자기 어지럽고 식은땀이 흐른다. 잘못된 대처는?', '["민간요법을 따르면 대부분 저절로 낫는다", "운전해서 바람 쐬러 나간다", "안전한 곳에 앉아 휴식", "수분 섭취"]', 1, '', '어지럼·실신 위험이 있어 운전은 금지입니다.', '보통', 2),
(31, '민간요법·오해', '감기에 땀을 많이 빼면 빨리 낫는다?', '["땀을 많이 빼야만 낫는다", "과학적 근거가 부족하며 휴식·수분 보충이 우선이다", "증상만 줄면 항생제는 스스로 중단해도 된다", "대체로 맞다"]', 1, '', '발한이 병을 ‘배출’한다는 증거는 부족합니다.', '보통', 2),
(32, '민간요법·오해', '비타민 C를 많이 먹으면 감기가 ‘치료’된다?', '["증상만 줄면 항생제는 스스로 중단해도 된다", "일부에서 기간 단축 보고가 있으나 만능 치료는 아니다", "고용량이면 치료된다", "오히려 감기를 유발한다"]', 1, '', '예방·치료 효과는 제한적이고 개인차가 큽니다.', '보통', 2),
(33, '민간요법·오해', '화상 부위에 치약/된장을 바르면 좋다?', '["감염·자극 위험이 있어 권장되지 않는다", "상처가 크면만 안 된다", "도움이 된다", "치약이나 된장을 바르면 화상이 가라앉는다"]', 0, '', '이물·세균으로 상태를 악화시킬 수 있습니다.', '어려움', 3),
